import "./App.css";
import Navbar from "./components(TextUtils)/Navbar";
import About from "./components(TextUtils)/About";
import TextForm from "./components(TextUtils)/TextForm";
import React, { useState } from "react";
import { message } from "antd";
import { Route, Routes } from "react-router-dom";

function App() {
  const [mode, setmode] = useState("light");
  const [alert, setAlert] = useState(null);
  const [msg, Alert] = message.useMessage();

  const showAlert = (message, type) => {
    setAlert({
      message: message,
      type: type,
    });
    msg.open({
      type: type,
      content: message,
    });
    setTimeout(() => {
      setAlert(null);
    }, 100);
  };

  const toggleMode = () => {
    if (mode === "light") {
      setmode("dark");
      document.body.style.backgroundColor = "#042743";
      document.body.style.color = "white";
      showAlert("Dark mode has been enabled!", "success");
      document.title = "TextUtils - Darkmode";
    } else {
      setmode("light");
      document.body.style.backgroundColor = "white";
      document.body.style.color = "grey";
      showAlert("Light mode has been enabled!", "success");
      document.title = "TextUtils - Lightmode";
    }
  };
  return (
    <>
      {/* <TextForm
        showAlert={showAlert}
        heading="Enter the Text to analyze"
        mode={mode}
      />

      <div className="container my-3">
        <About />
      </div> */}
      <Navbar toggleMode={toggleMode} />
      <Routes>
        <Route
          path="/*"
          element={
            <TextForm
              showAlert={showAlert}
              heading="Enter the Text to analyze"
              mode={mode}
            />
          }
        />

        <Route
          path="/about"
          showAlert={showAlert}
          element={<About toggleMode={toggleMode} />}
        />
      </Routes>
    </>
  );
}

export default App;
