import "./App.css";
import Navbar from "./components(TextUtils)/Navbar";
// import About from "./components(TextUtils)/About";
import TextForm from "./components(TextUtils)/TextForm";
import React, { useState } from "react";

function App() {
  const [mode, setmode] = useState("dark");

  const toggleMode = () => {
    if (mode === "light") {
      setmode = "dark";
    } else {
      setmode = "light";
    }
  };
  return (
    <>
      {/* <Navbar /> */}
      <Navbar title="TextUtils" home="Home" about="" mode={mode} toggleMode={toggleMode} />

      <div className="container my-3">
        <TextForm heading="Enter the Text to analyze" />
        {/* <About/> */}
      </div>
    </>
  );
}

export default App;
