import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function About(props) {
  console.log(props.mode);
  return (
    <div
      className={`container ${props.mode}`}
      // style={{ backgroundColor: props.mode === "light" ? "grey" : "white" }}
      onClick={props.togglemode}
    >
      <h2>About us</h2>
      <div
        className={`accordion ${props.mode}`}
        id="accordionExample"
        style={{
          backgroundColor: props.mode === "light" ? "white" : "#042743",
        }}
      >
        <div className="accordion-item">
          <h2 className="accordion-header">
            <button
              className={`accordion-button ${props.mode}`}
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#collapseOne"
              aria-expanded="true"
              aria-controls="collapseOne"
              style={
                {
                  // backgroundColor: props.mode === "light" ? "black" : "white",
                  // Color: props.mode === "light" ? "white" : "grey",
                }
              }
              // onClick={props.toggleMode}
            >
              Accordion Item #1
            </button>
          </h2>
          <div
            id="collapseOne"
            className={`accordion-collapse collapse show ${props.mode}`}
            data-bs-parent="#accordionExample"
            style={
              {
                // backgroundColor: props.mode === "light" ? "black" : "white",
              }
            }
          >
            {/* Accordion 1 body */}
            <div
              className="accordion-body"
              style={{
                backgroundColor: props.mode === "light" ? "white" : "#042743",
                color: props.mode === "light" ? "black" : "white",
              }}
            >
              <strong>This is the first item's accordion body.</strong> It is
              shown by default, until the collapse plugin adds the appropriate
              classNamees that we use to style each element. These classNamees
              control the overall appearance, as well as the showing and hiding
              via CSS transitions. You can modify any of this with custom CSS or
              overriding our default variables. It's also worth noting that just
              about any HTML can go within the <code>.accordion-body</code>,
              though the transition does limit overflow.
            </div>
          </div>
        </div>
        <div className="accordion-item">
          <h2 className="accordion-header">
            <button
              className="accordion-button collapsed"
              style={
                {
                  // backgroundColor: props.mode === "light" ? "black" : "white",
                }
              }
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#collapseTwo"
              aria-expanded="false"
              aria-controls="collapseTwo"
            >
              Accordion Item #2
            </button>
          </h2>
          <div
            id="collapseTwo"
            className="accordion-collapse collapse"
            data-bs-parent="#accordionExample"
          >
            {/* Accordion 2 body */}
            <div
              className="accordion-body"
              style={
                {
                  // backgroundColor: props.mode === "light" ? "black" : "white",
                }
              }
            >
              <strong>This is the second item's accordion body.</strong> It is
              hidden by default, until the collapse plugin adds the appropriate
              classNamees that we use to style each element. These classNamees
              control the overall appearance, as well as the showing and hiding
              via CSS transitions. You can modify any of this with custom CSS or
              overriding our default variables. It's also worth noting that just
              about any HTML can go within the <code>.accordion-body</code>,
              though the transition does limit overflow.
            </div>
          </div>
        </div>
        <div className="accordion-item">
          <h2 className="accordion-header">
            <button
              className="accordion-button collapsed "
              // mode={mode}
              style={
                {
                  // backgroundColor: props.mode === "light" ? "black" : "white",
                  // color: props.mode === "light" ? "black" : "white",
                }
              }
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#collapseThree"
              aria-expanded="false"
              aria-controls="collapseThree"
              onClick={props.toggleMode}
            >
              Accordion Item #3
            </button>
          </h2>
          <div
            id="collapseThree"
            className="accordion-collapse collapse"
            style={
              {
                // backgroundColor: props.mode === "light" ? "black" : "white",
              }
            }
            data-bs-parent="#accordionExample"
          >
            {/* Accordion 3 body */}
            <div className="accordion-body" onClick={props.togglemode}>
              <strong>This is the third item's accordion body.</strong> It is
              hidden by default, until the collapse plugin adds the appropriate
              classNamees that we use to style each element. These classNamees
              control the overall appearance, as well as the showing and hiding
              via CSS transitions. You can modify any of this with custom CSS or
              overriding our default variables. It's also worth noting that just
              about any HTML can go within the <code>.accordion-body</code>,
              though the transition does limit overflow.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
